mapper.select:
select * from tb_user a
where 1=1
and a.account = #{account}

mapper.update:
update tb_user set name=#{name},
password=#{password},
number = #{number}, roleid=#{roleid}, userdata=#{userdata} where id=#{userid}

mapper.insert:
    insert into tb_role(
    	name
    )values(
    	#{name}
    )

		SELECT LAST_INSERT_ID() AS ID
