mapper.select:
select count(*) from ( SELECT a.regid, a.regname, a.regnumber, a.regschedule, b.name as regprovince, c.name as regcity, d.name as regtown FROM tb_quyuxingxiangmu a left join tb_area_province b on a.regprovince=b.code left join tb_area_city c on a.regcity=c.code left join tb_area_town d on a.regtown=d.code where 1=1
and (a.regname like '%${search}%' or a.regnumber like '%${search}%' or a.regschedule like '%${search}%')
) t where 1=1
