SELECT a.orgid, a.orgnumber, a.orgcategory, a.orgname, b.name as orgprovince, c.name as orgcity, d.name as orgtown, a.orgentry FROM tb_shehuizuzhidanwei a LEFT JOIN tb_area_province b on a.orgprovince=b.code LEFT JOIN tb_area_city c on a.orgcity=c.code LEFT JOIN tb_area_town d on a.orgtown=d.code where 1=1 and a.visual=1
