mapper.select:
select count(*) from ( select a.feanumber,a.feaname,a.fealevel,a.feaid, b.name as feaprovince,c.name as feacity,d.name as featown from Tb_tesexiaozhen a LEFT JOIN tb_area_province b on a.feaprovince=b.code LEFT JOIN tb_area_city c on a.feacity=c.code LEFT JOIN tb_area_town d on a.featown=d.code where 1=1
and (a.feaname like '%${search}%' or a.feanumber like '%${search}%' or a.fealevel like '%${search}%')
)t where 1=1
