mapper.select:
SELECT a.invid, a.invnumber, a.invcharge, a.invname, b.name as invprovince, c.name as invcity, d.name as invtown, a.inventry FROM tb_zhaoshangxiangmu a LEFT JOIN tb_area_province b on a.invprovince=b.code LEFT JOIN tb_area_city c on a.invcity=c.code LEFT JOIN tb_area_town d on a.invtown=d.code where 1=1 and a.visual=1
${supersearch}
order by b.code,c.code,d.code,a.createtime desc,a.invid desc

select count(*) from ( SELECT a.invid, a.invnumber, a.invcharge, a.invname, b.name as invprovince, c.name as invcity, d.name as invtown FROM tb_zhaoshangxiangmu a LEFT JOIN tb_area_province b on a.invprovince=b.code LEFT JOIN tb_area_city c on a.invcity=c.code LEFT JOIN tb_area_town d on a.invtown=d.code where 1=1 and a.visual=1
${supersearch}
)t where 1=1
