mapper.select:
select count(*) from ( SELECT a.centertownid, a.centertownname, a.number, a.townlevel, b. NAME AS sys_province, c. NAME AS sys_city, d. NAME AS sys_town FROM tb_zhongxinzhen a LEFT JOIN tb_area_province b ON a.province = b. code LEFT JOIN tb_area_city c ON a.city = c. code LEFT JOIN tb_area_town d ON a.town = d. code where 1=1 and a.visual=1
and (a.number like '%${search}%' or a.centertownname like '%${search}%' or a.townlevel like '%${search}%')
) t
